mapper.select:
select rd.id , rd.uid,rd.name,rd.driver_class driverClass, rd.jdbc_url jdbcUrl , rd.user ,rd.password , rd.queryer_class queryerClass ,rd.pool_class poolClass ,rd.options , rd.`comment` , rd.create_user createUser,rd.create_date createDate, ru.name updateUserName , rd.update_user updateUser , rd.update_date updateDate from rh_datasource rd left join rh_user ru on rd.update_user=ru.id where 1=1
order by rd.id
limit ${startRowIndex} , ${pageSize}
