mapper.select:
select rr.id, rr.uid, category_id categoryId, ds_id dsId, rr.name, sql_text sqlText, meta_columns metaColumns, query_params queryParams, rr.options, report_explain reportExplain, rr.status, rr.sequence, rr.comment, rr.create_user createUser, rr.create_date createDate, rr.update_user updateUser, rr.update_date updateDate, rd.name dsName , ru.name updateUserName from rh_report rr left join rh_datasource rd on rr.ds_id=rd.id left join rh_user ru on rr.update_user=ru.id where rr.`status`=1
order by rr.sequence , rr.id
limit ${startRowIndex} , ${pageSize}
